INSERT INTO `config_info` VALUES (108, 'lersosa-service-job.yml', 'SERVICE_GROUP', 'spring:\n  datasource:\n    dynamic:\n      # 设置默认的数据源或者数据源组,默认值即为 master\n      primary: master\n      seata: false\n      datasource:\n        # 主库数据源\n        master:\n          type: ${spring.datasource.type}\n          driver-class-name: com.mysql.cj.jdbc.Driver\n          url: ${datasource.job.url}\n          username: ${datasource.job.username}\n          password: ${datasource.job.password}\n\nsnail-job:\n  enabled: true\n  # 需要在 SnailJob 后台组管理创建对应名称的组,然后创建任务的时候选择对应的组,才能正确分派任务\n  group: \"lersosa_group\"\n  #  SnailJob 接入验证令牌\n  token: \"SJ_cKqBTPzCsWA3VyuCfFoccmuIEGXjr5KT\"\n  server:\n    # 从 nacos 获取服务\n    server-name: lersosa-visual-snailjob\n    # 服务名优先 ip垫底\n    host: lersosa-snailjob\n    port: 17888\n  # 详见 script/sql/ry_job.sql `sj_namespace` 表\n  namespace: ${spring.profiles.active}\n  # 随主应用端口飘逸\n  port: 2${server.port}\n  # 客户端ip指定\n  host:\n', 'ba13a3d8f7df5321cc54378ea4a74c83', '2022-01-09 15:23:00', '2025-08-07 23:29:05', 'nacos', '0:0:0:0:0:0:0:1', '', '356d484c-399c-4a23-9419-e200e8edbff9', '定时任务', '', '', 'yaml', '', '');
INSERT INTO `config_info` VALUES (109, 'lersosa-service-resource.yml', 'SERVICE_GROUP', 'spring:\n  datasource:\n    dynamic:\n      # 设置默认的数据源或者数据源组,默认值即为 master\n      primary: master\n      datasource:\n        # 主库数据源\n        master:\n          type: ${spring.datasource.type}\n          driver-class-name: com.mysql.cj.jdbc.Driver\n          url: ${datasource.system-master.url}\n          username: ${datasource.system-master.username}\n          password: ${datasource.system-master.password}\n#        oracle:\n#          type: ${spring.datasource.type}\n#          driverClassName: oracle.jdbc.OracleDriver\n#          url: ${datasource.system-oracle.url}\n#          username: ${datasource.system-oracle.username}\n#          password: ${datasource.system-oracle.password}\n#        postgres:\n#          type: ${spring.datasource.type}\n#          driverClassName: org.postgresql.Driver\n#          url: ${datasource.system-postgres.url}\n#          username: ${datasource.system-postgres.username}\n#          password: ${datasource.system-postgres.password}\n\n# 默认/推荐使用sse推送\nsse:\n  enabled: true\n  path: /sse\n\nwebsocket:\n  # 如果关闭 需要和前端开关一起关闭\n  enabled: false\n  # 路径\n  path: /websocket\n  # 设置访问源地址\n  allowedOrigins: \'*\'\n\nmail:\n  enabled: false\n  host: smtp.163.com\n  port: 465\n  # 是否需要用户名密码验证\n  auth: true\n  # 发送方，遵循RFC-822标准\n  from: xxx@163.com\n  # 用户名（注意：如果使用foxmail邮箱，此处user为qq号）\n  user: xxx@163.com\n  # 密码（注意，某些邮箱需要为SMTP服务单独设置密码，详情查看相关帮助）\n  pass: xxxxxxxxxx\n  # 使用 STARTTLS安全连接，STARTTLS是对纯文本通信协议的扩展。\n  starttlsEnable: true\n  # 使用SSL安全连接\n  sslEnable: true\n  # SMTP超时时长，单位毫秒，缺省值不超时\n  timeout: 0\n  # Socket连接超时值，单位毫秒，缺省值不超时\n  connectionTimeout: 0\n\n# sms 短信 支持 阿里云 腾讯云 云片 等等各式各样的短信服务商\n# https://sms4j.com/doc3/ 差异配置文档地址 支持单厂商多配置，可以配置多个同时使用\nsms:\n  # 配置源类型用于标定配置来源(interface,yaml)\n  config-type: yaml\n  # 用于标定yml中的配置是否开启短信拦截，接口配置不受此限制\n  restricted: true\n  # 短信拦截限制单手机号每分钟最大发送，只对开启了拦截的配置有效\n  minute-max: 1\n  # 短信拦截限制单手机号每日最大发送量，只对开启了拦截的配置有效\n  account-max: 30\n  # 以下配置来自于 org.dromara.sms4j.provider.config.BaseConfig类中\n  blends:\n    # 唯一ID 用于发送短信寻找具体配置 随便定义别用中文即可\n    # 可以同时存在两个相同厂商 例如: ali1 ali2 两个不同的阿里短信账号 也可用于区分租户\n    config1:\n      # 框架定义的厂商名称标识，标定此配置是哪个厂商，详细请看厂商标识介绍部分\n      supplier: alibaba\n      # 有些称为accessKey有些称之为apiKey，也有称为sdkKey或者appId。\n      access-key-id: 您的accessKey\n      # 称为accessSecret有些称之为apiSecret\n      access-key-secret: 您的accessKeySecret\n      signature: 您的短信签名\n      sdk-app-id: 您的sdkAppId\n    config2:\n      # 厂商标识，标定此配置是哪个厂商，详细请看厂商标识介绍部分\n      supplier: tencent\n      access-key-id: 您的accessKey\n      access-key-secret: 您的accessKeySecret\n      signature: 您的短信签名\n      sdk-app-id: 您的sdkAppId\n', 'efb16000eb230328fa2128ea56558fd2', '2022-01-09 15:23:00', '2025-08-07 23:29:19', 'nacos', '0:0:0:0:0:0:0:1', '', '356d484c-399c-4a23-9419-e200e8edbff9', '文件服务', '', '', 'yaml', '', '');
INSERT INTO `config_info` VALUES (110, 'lersosa-service-workflow.yml', 'SERVICE_GROUP', 'spring:\n  datasource:\n    dynamic:\n      # 设置默认的数据源或者数据源组,默认值即为 master\n      primary: master\n      datasource:\n        # 主库数据源\n        master:\n          type: ${spring.datasource.type}\n          driver-class-name: com.mysql.cj.jdbc.Driver\n          url: ${datasource.workflow.url}\n          username: ${datasource.workflow.username}\n          password: ${datasource.workflow.password}\n#        oracle:\n#          type: ${spring.datasource.type}\n#          driverClassName: oracle.jdbc.OracleDriver\n#          url: ${datasource.system-oracle.url}\n#          username: ${datasource.system-oracle.username}\n#          password: ${datasource.system-oracle.password}\n#        postgres:\n#          type: ${spring.datasource.type}\n#          driverClassName: org.postgresql.Driver\n#          url: ${datasource.system-postgres.url}\n#          username: ${datasource.system-postgres.username}\n#          password: ${datasource.system-postgres.password}\n\n# flowable配置\nflowable:\n  # 开关 用于启动/停用工作流\n  enabled: true\n  process.enabled: ${flowable.enabled}\n  eventregistry.enabled: ${flowable.enabled}\n  # 关闭定时任务JOB\n  async-executor-activate: false\n  # 将databaseSchemaUpdate设置为true。当Flowable发现库与数据库表结构不一致时，会自动将数据库表结构升级至新版本。\n  database-schema-update: true\n  activity-font-name: 宋体\n  label-font-name: 宋体\n  annotation-font-name: 宋体\n  # 关闭各个模块生成表，目前只使用工作流基础表\n  idm:\n    enabled: false\n  cmmn:\n    enabled: false\n  dmn:\n    enabled: false\n  app:\n    enabled: false\n', '95b566892b838da618fbfdaf1ecffd0a', '2022-01-09 15:23:00', '2025-08-07 23:30:00', 'nacos', '0:0:0:0:0:0:0:1', '', '356d484c-399c-4a23-9419-e200e8edbff9', '工作流服务', '', '', 'yaml', '', '');
