INSERT INTO `config_info` VALUES (114, 'lersosa-visual-snailjob.yml', 'VISUAL_GROUP', 'spring:\n  datasource:\n    type: com.zaxxer.hikari.HikariDataSource\n    driver-class-name: com.mysql.cj.jdbc.Driver\n    url: ${datasource.job.url}\n    username: ${datasource.job.username}\n    password: ${datasource.job.password}\n    hikari:\n      connection-timeout: 30000\n      validation-timeout: 5000\n      minimum-idle: 10\n      maximum-pool-size: 20\n      idle-timeout: 600000\n      max-lifetime: 900000\n      keepaliveTime: 30000\n  cloud:\n    nacos:\n      discovery:\n        metadata:\n          # 解决 er 服务有 context-path 无法监控问题\n          management.context-path: ${server.servlet.context-path}/actuator\n          # 监控账号密码\n          username: lersosa\n          userpassword: Zcx@223852//\n\n# snail-job 服务端配置\nsnail-job:\n  # 拉取重试数据的每批次的大小\n  retry-pull-page-size: 1000\n  # 拉取重试数据的每批次的大小\n  job-pull-page-size: 1000\n  # 服务端 netty 端口\n  netty-port: 17888\n  # 重试和死信表的分区总数\n  total-partition: 2\n  # 一个客户端每秒最多接收的重试数量指令\n  limiter: 1000\n  # 号段模式下步长配置\n  step: 100\n  # 日志保存时间(单位: day)\n  log-storage: 90\n  # 回调配置\n  callback:\n    #回调最大执行次数\n    max-count: 288\n    #间隔时间\n    trigger-interval: 900\n  retry-max-pull-count: 10\n', '48b4e04732a1f048180a2826c715a63d', '2022-01-09 15:21:02', '2025-08-07 23:31:27', 'nacos', '0:0:0:0:0:0:0:1', '', '356d484c-399c-4a23-9419-e200e8edbff9', 'SJ定时任务控制台', '', '', 'yaml', '', '');
