INSERT INTO `his_config_info` VALUES (3, 33, 'lersosa-gateway-api.yml', 'GATEWAY_GROUP', '', '# 安全配置\nsecurity:\n  # 不校验白名单\n  ignore:\n    whites:\n      - /python/*\n      - /auth/code\n      - /auth/logout\n      - /auth/login\n      - /auth/binding/*\n      - /auth/social/callback\n      - /auth/register\n      - /auth/tenant/list\n      - /resource/sms/code\n      - /resource/sse/close\n      - /doc/swagger-ui/*\n      - /v3/api-docs/**\n      - /*/v3/api-docs/**\n      - /*/error\n      - /csrf\n\nspring:\n  cloud:\n    # 网关配置\n    gateway:\n      # 打印请求日志(自定义)\n      requestLog: true\n      discovery:\n        locator:\n          lowerCaseServiceId: true\n          enabled: true\n      routes:\n        # 认证中心\n        - id: lersosa-service-auth\n          uri: lb://lersosa-service-auth\n          predicates:\n            - Path=/auth/**\n          filters:\n            - StripPrefix=1\n        # 代码生成\n        - id: lersosa-service-gen\n          uri: lb://lersosa-service-gen\n          predicates:\n            - Path=/tool/**\n          filters:\n            - StripPrefix=1\n        # 系统模块\n        - id: lersosa-service-system\n          uri: lb://lersosa-service-system\n          predicates:\n            - Path=/system/**,/monitor/**\n          filters:\n            - StripPrefix=1\n        # 资源服务\n        - id: lersosa-service-resource\n          uri: lb://lersosa-service-resource\n          predicates:\n            - Path=/resource/**\n          filters:\n            - StripPrefix=1\n        # workflow服务\n        - id: lersosa-service-workflow\n          uri: lb://lersosa-service-workflow\n          predicates:\n            - Path=/workflow/**\n          filters:\n            - StripPrefix=1\n        # 演示服务\n        - id: lersosa-service-demo\n          uri: lb://lersosa-service-demo\n          predicates:\n            - Path=/demo/**\n          filters:\n            - StripPrefix=1\n        # MQ演示服务\n        - id: lersosa-service-test-mq\n          uri: lb://lersosa-service-test-mq\n          predicates:\n            - Path=/test-mq/**\n          filters:\n            - StripPrefix=1\n        # pulsar 服务\n        - id: lersosa-service-pulsar\n          uri: lb://lersosa-service-pulsar\n          predicates:\n            - Path=/pulsar/**\n          filters:\n            - StripPrefix=1\n        # python 服务\n        - id: lersosa-service-python\n          uri: lb://lersosa-service-python\n          predicates:\n            - Path=/python/**\n          filters:\n            - StripPrefix=1\n\n    # sentinel 配置\n    sentinel:\n      filter:\n        enabled: false\n      # nacos配置持久化\n      datasource:\n        ds1:\n          nacos:\n            server-addr: ${spring.cloud.nacos.server-addr}\n            dataId: sentinel-${spring.application.name}.json\n            groupId: ${spring.cloud.nacos.config.group}\n            username: ${spring.cloud.nacos.username}\n            password: ${spring.cloud.nacos.password}\n            namespace: ${spring.profiles.active}\n            data-type: json\n            rule-type: gw-flow\n\nspringdoc:\n  api-docs:\n    ignore:\n      excludes:\n        - lersosa-gateway-api\n        - lersosa-service-job\n        - lersosa-service-grpc\n        - lersosa-service-workflow\n', '42503c8f868136ae2e4cd4929f98b117', '2025-08-03 15:04:18', '2025-08-03 07:04:50', 'nacos', '0:0:0:0:0:0:0:1', 'U', '106fdced-d95e-46db-b9f4-0abdce58a18b', '', 'formal', '', '{\"type\":\"yaml\",\"src_user\":\"nacos\",\"c_desc\":\"网关模块\"}');
