INSERT INTO `his_config_info` VALUES (3, 45, 'lersosa-gateway-api.yml', 'GATEWAY_GROUP', '', '# 安全配置\nsecurity:\n  # 不校验白名单\n  ignore:\n    whites:\n      - /python/*\n      - /auth/code\n      - /auth/logout\n      - /auth/login\n      - /auth/binding/*\n      - /auth/social/callback\n      - /auth/register\n      - /auth/tenant/list\n      - /resource/sms/code\n      - /resource/sse/close\n      - /doc/swagger-ui/*\n      - /v3/api-docs/**\n      - /*/v3/api-docs/**\n      - /*/error\n      - /csrf\n\nspring:\n  cloud:\n    # 网关配置\n    gateway:\n      # 打印请求日志(自定义)\n      requestLog: true\n      discovery:\n        locator:\n          lowerCaseServiceId: true\n          enabled: true\n      routes:\n        # 认证中心\n        - id: lersosa-service-auth\n          uri: lb://lersosa-service-auth\n          predicates:\n            - Path=/auth/**\n          filters:\n            - StripPrefix=1\n        # 代码生成\n        - id: lersosa-service-gen\n          uri: lb://lersosa-service-gen\n          predicates:\n            - Path=/tool/**\n          filters:\n            - StripPrefix=1\n        # 系统模块\n        - id: lersosa-service-system\n          uri: lb://lersosa-service-system\n          predicates:\n            - Path=/system/**,/monitor/**\n          filters:\n            - StripPrefix=1\n        # 资源服务\n        - id: lersosa-service-resource\n          uri: lb://lersosa-service-resource\n          predicates:\n            - Path=/resource/**\n          filters:\n            - StripPrefix=1\n        # workflow服务\n        - id: lersosa-service-workflow\n          uri: lb://lersosa-service-workflow\n          predicates:\n            - Path=/workflow/**\n          filters:\n            - StripPrefix=1\n        # 演示服务\n        - id: lersosa-service-demo\n          uri: lb://lersosa-service-demo\n          predicates:\n            - Path=/demo/**\n          filters:\n            - StripPrefix=1\n        # MQ演示服务\n        - id: lersosa-service-test-mq\n          uri: lb://lersosa-service-test-mq\n          predicates:\n            - Path=/test-mq/**\n          filters:\n            - StripPrefix=1\n        # pulsar 服务\n        - id: lersosa-service-pulsar\n          uri: lb://lersosa-service-pulsar\n          predicates:\n            - Path=/pulsar/**\n          filters:\n            - StripPrefix=1\n        # python 服务\n        - id: lersosa-service-python\n          uri: lb://lersosa-service-python\n          predicates:\n            - Path=/python/**\n          filters:\n            - StripPrefix=1\n\n    # sentinel 配置\n    sentinel:\n      filter:\n        enabled: false\n      # nacos配置持久化\n      datasource:\n        ds1:\n          nacos:\n            server-addr: ${spring.cloud.nacos.server-addr}\n            dataId: sentinel-${spring.application.name}.json\n            groupId: ${spring.cloud.nacos.config.group}\n            username: ${spring.cloud.nacos.username}\n            password: ${spring.cloud.nacos.password}\n            namespace: ${spring.profiles.active}\n            data-type: json\n            rule-type: gw-flow\n\n  datasource:\n    dynamic:\n      primary: system-master\n      strict: true\n      p6spy: true\n      seata: ${seata.enabled}\n      hikari:\n        maxPoolSize: 20\n        minIdle: 10\n        connectionTimeout: 30000\n        validationTimeout: 5000\n        idleTimeout: 600000\n        maxLifetime: 1800000\n        keepaliveTime: 30000\n      datasource:\n        system-master:\n          url: jdbc:mysql://localhost:3306/lersosa-cloud?useUnicode=true&characterEncoding=utf8mb4&zeroDateTimeBehavior=convertToNull&useSSL=false&serverTimezone=GMT%2B8&rewriteBatchedStatements=true&allowPublicKeyRetrieval=true\n          username: root\n          password: Zcx@223852//\n        gen:\n          url: jdbc:mysql://localhost:3306/lersosa-cloud?useUnicode=true&characterEncoding=utf8mb4&zeroDateTimeBehavior=convertToNull&useSSL=false&serverTimezone=GMT%2B8&rewriteBatchedStatements=true&allowPublicKeyRe